substring = "AGATC"
s = ""
i = 0
streak = 0
longeststreak = 0
while i < len(s):
    if s[i: i + len(substring)] == substring:
        streak += 1
        i = i + len(substring)
    else:
        i = i + 1
        if longeststreak < streak:
            longeststreak = streak
            streak = 0
        else:
            streak = 0
print(longeststreak)